var ccs_slideIndex=1;window.addEventListener("load",ccs_setupSlider);function ccs_setupSlider(){ccs_addControls(),ccs_addPositions(),ccs_showSlide(ccs_slideIndex)}function ccs_jumpSlide(e){ccs_showSlide(e)}function ccs_slideToTheRight(){var e=ccs_slideIndex+=1,c=document.getElementsByClassName("cc-slider_item");e>c.length&&(ccs_slideIndex=1),e<1&&(ccs_slideIndex=c.length),ccs_showSlide(e)}function ccs_slideToTheLeft(){var e=ccs_slideIndex-=1,c=document.getElementsByClassName("cc-slider_item");e>c.length&&(ccs_slideIndex=1),e<1&&(ccs_slideIndex=c.length),ccs_showSlide(e)}function ccs_showSlide(e){var c,s=document.getElementsByClassName("cc-slider_item"),l=document.getElementsByClassName("cc-slider_dot");for(ccs_slideIndex=e>s.length?1:e<1?s.length:e,c=0;c<s.length;c++)s[c].style.display="none";for(c=0;c<l.length;c++)l[c].className=l[c].className.replace(" cc-slider_active","");s[ccs_slideIndex-1].style.display="block",l[ccs_slideIndex-1].className+=" cc-slider_active"}function ccs_addPositions(){for(var e=document.getElementsByClassName("cc-slider_item"),c=e.length,s=0;s<c;s++)e[s].innerHTML+='<div class="cc-slider_position">'+(s+1)+"/"+c+"</div>"}function ccs_addControls(){document.getElementById("cc-slider").innerHTML+='<a class="cc-slider_prev" onclick="ccs_slideToTheLeft()">&#10094;</a><a class="cc-slider_next" onclick="ccs_slideToTheRight()">&#10095;</a>'}